const useStyles = makeStyles((theme) => ({
  gridContainer: {
    marginTop: "50px",
    [theme.breakpoints.down("sm")]: {
      margin: 0,
    },
  },
  card: {
    marginTop: "5vh",
    marginBottom: "5vh",
    marginLeft: "auto",
    marginRight: "auto",
    height: "auto",
    width: "80vw",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    [theme.breakpoints.up("sm")]: {
      height: "355px",
    },
  },
}));
