import { useEffect, useState } from "react";
import axios from 'axios';
import { Grid, Card, CardMedia, Typography, makeStyles } from '@material-ui/core';

const useStyles = makeStyles({
  gridContainer: {
    marginTop: "50px",
    marginLeft: "150px",
  },
  card: {
    marginTop: "120px",
    marginLeft: "100px",
    height: "355px",
    width: "500px",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
});

const GridDisplay = () => {
  const classes = useStyles();
  const [users, setUsers] = useState([]);


  useEffect(() => {
    axios
      .get("https://express-t4.onrender.com/api/users")
      .then((response) => {
        const data = response.data;
        setUsers(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleCardClick = (id) => {
    axios
      .get(`https://express-t4.onrender.com/api/users/${id}`)
      .then((response) => {
        const data = response.data;
        console.log(data);

      })
      .catch((error) => {
        console.log(error);
      });
  };


  return (
    <Grid container spacing={2}>
      {users.map(user => (
        <Grid item xs={12} sm={6} md={4} key={user._id} className={classes.gridContainer}>
          <Card className={classes.card} onClick={() => handleCardClick(user._id)}>
            <CardMedia
              component="img"
              height="194"
              image={user.picture}
              alt={user.name}
              style={{ objectFit: 'cover' }}
            />
            <Typography variant="h6">{user.name}</Typography>
            <Typography variant="subtitle1">{user.email}</Typography>
            <Typography variant="body2">{user.address}</Typography>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};



export default GridDisplay;
